<template>
  <div class="ffs-demo">
    <h1>Feige-Fiat-Shamir Protokoll Demonstrator</h1>

    <div class="setup-section">
      <h2>Setup</h2>
      <div class="form-group">
        <label for="k-value">Anzahl der Zahlen (k):</label>
        <input id="k-value" v-model.number="k" type="number" min="1" max="10" @change="resetDemo">
      </div>
      <div class="form-group">
        <label for="n-value">Modulus (n = p * q):</label>
        <input id="n-value" v-model.number="n" type="number" min="10" @change="resetDemo">
        <button @click="generateValues" class="btn">Werte generieren</button>
      </div>

      <div v-if="setup.complete" class="values-display">
        <h3>Peggy's geheime Werte:</h3>
        <div class="secret-values">
          <div v-for="(s, index) in setup.secretValues" :key="'s'+index" class="value-item">
            <span>s<sub>{{index+1}}</sub> = {{s}}</span>
          </div>
        </div>

        <h3>Öffentliche Werte:</h3>
        <div class="public-values">
          <div v-for="(v, index) in setup.publicValues" :key="'v'+index" class="value-item">
            <span>v<sub>{{index+1}}</sub> = {{v}}</span>
          </div>
        </div>
      </div>
    </div>

    <div class="protocol-section" v-if="setup.complete">
      <h2>Protokoll-Ablauf</h2>
      <div class="protocol-controls">
        <button @click="startNewRound" :disabled="roundActive" class="btn primary">Neue Runde starten</button>
        <button @click="resetDemo" class="btn secondary">Zurücksetzen</button>
      </div>

      <div class="protocol-visualization">
        <div class="character peggy" :class="{'active': currentStep === 'commitment' || currentStep === 'response'}">
          <img src="@/assets/peggy.png" alt="Peggy">
          <div class="character-name">Peggy (Beweiser)</div>
          <div class="thought-bubble" v-if="currentStep === 'commitment' || currentStep === 'response'">
            <p v-if="currentStep === 'commitment'">
              Ich wähle ein zufälliges r: <strong>{{currentRound.r}}</strong><br>
              Berechne r² mod n: <strong>{{currentRound.x}}</strong>
            </p>
            <p v-if="currentStep === 'response' && currentRound.e === 0">
              Victor hat e=0 gewählt.<br>
              Ich sende y = r: <strong>{{currentRound.y}}</strong>
            </p>
            <p v-if="currentStep === 'response' && currentRound.e === 1">
              Victor hat e=1 gewählt.<br>
              Ich sende y = r·s<sub>j</sub> mod n: <strong>{{currentRound.y}}</strong>
            </p>
          </div>
        </div>

        <div class="protocol-messages">
          <div class="message" v-if="currentStep === 'commitment'">
            <div class="arrow right"></div>
            <div class="content">
              <strong>1. Commitment:</strong> x = {{currentRound.x}}
            </div>
          </div>

          <div class="message" v-if="currentStep === 'challenge'">
            <div class="arrow left"></div>
            <div class="content">
              <strong>2. Challenge:</strong> e = {{currentRound.e}}
              <span v-if="currentRound.j">, j = {{currentRound.j}}</span>
            </div>
          </div>

          <div class="message" v-if="currentStep === 'response'">
            <div class="arrow right"></div>
            <div class="content">
              <strong>3. Antwort:</strong> y = {{currentRound.y}}
            </div>
          </div>

          <div class="message" v-if="currentStep === 'verification'">
            <div class="arrow none"></div>
            <div class="content verification" :class="{'success': currentRound.verified, 'failure': !currentRound.verified}">
              <strong>4. Verifikation:</strong> 
              {{ currentRound.verified ? '✓ Bestätigt!' : '✗ Fehlgeschlagen!' }}
              <div class="verification-equation">
                {{ currentRound.e === 0 ? 'y² mod n = ' : 'y² mod n = x · v_j mod n' }}
                <br>
                {{ currentRound.verificationLeft }} = {{ currentRound.verificationRight }}
              </div>
            </div>
          </div>
        </div>

        <div class="character victor" :class="{'active': currentStep === 'challenge' || currentStep === 'verification'}">
          <img src="../assets/victor.png" alt="Victor">
          <div class="character-name">Victor (Verifizierer)</div>
          <div class="thought-bubble" v-if="currentStep === 'challenge' || currentStep === 'verification'">
            <p v-if="currentStep === 'challenge'">
              Ich wähle zufällig:<br>
              e = <strong>{{currentRound.e}}</strong>
              <span v-if="currentRound.e === 1">und j = <strong>{{currentRound.j}}</strong></span>
            </p>
            <p v-if="currentStep === 'verification'">
              Prüfe: {{ currentRound.e === 0 ? 'y² mod n = x' : 'y² mod n = x · v_j mod n' }}<br>
              {{ currentRound.verified ? '✓ Stimmt!' : '✗ Stimmt nicht!' }}
            </p>
          </div>
        </div>
      </div>

      <div class="protocol-steps" v-if="roundActive">
        <button 
          v-if="currentStep === 'start'" 
          @click="createCommitment" 
          class="btn step-btn">
          1. Peggy erstellt Commitment
        </button>

        <button 
          v-if="currentStep === 'commitment'" 
          @click="createChallenge" 
          class="btn step-btn">
          2. Victor sendet Challenge
        </button>

        <button 
          v-if="currentStep === 'challenge'" 
          @click="createResponse" 
          class="btn step-btn">
          3. Peggy sendet Antwort
        </button>

        <button 
          v-if="currentStep === 'response'" 
          @click="verifyResponse" 
          class="btn step-btn">
          4. Victor verifiziert
        </button>
      </div>

      <div class="rounds-history" v-if="rounds.length > 0">
        <h3>Bisherige Runden</h3>
        <div class="rounds-table">
          <table>
            <thead>
              <tr>
                <th>Runde</th>
                <th>x</th>
                <th>e</th>
                <th>j</th>
                <th>y</th>
                <th>Ergebnis</th>
              </tr>
            </thead>
            <tbody>
              <tr v-for="(round, index) in rounds" :key="index">
                <td>{{index + 1}}</td>
                <td>{{round.x}}</td>
                <td>{{round.e}}</td>
                <td>{{round.e === 1 ? round.j : '-'}}</td>
                <td>{{round.y}}</td>
                <td :class="{'success': round.verified, 'failure': round.verified === false}">
                  {{round.verified === undefined ? '-' : (round.verified ? '✓' : '✗')}}
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        <div class="security-info">
          <p>
            <strong>Sicherheit:</strong> Nach {{rounds.filter(r => r.verified !== undefined).length}} Runden
            ist die Wahrscheinlichkeit, dass Peggy schummelt, höchstens 
            (1/2)<sup>{{rounds.filter(r => r.verified).length}}</sup> = 
            {{Math.pow(0.5, rounds.filter(r => r.verified).length).toFixed(8)}}
          </p>
        </div>
      </div>
    </div>

          <div class="explanation">
      <h2>Erklärung des Feige-Fiat-Shamir Protokolls</h2>
      <div class="explanation-content">
        <h3>Ziel</h3>
        <p>Peggy will Victor beweisen, dass sie die Quadratwurzeln von k Zahlen modulo n kennt, ohne diese preiszugeben.</p>

        <h3>Setup</h3>
        <ol>
          <li>Ein Modulus n sollte idealerweise ein Blum-Integer sein (Produkt zweier Primzahlen p, q mit p ≡ 3 mod 4 und q ≡ 3 mod 4)</li>
          <li>Peggy wählt geheime Werte s<sub>1</sub>, s<sub>2</sub>, ..., s<sub>k</sub>, die coprime zu n sind und idealerweise größer als √n</li>
          <li>Peggy berechnet öffentliche Werte v<sub>i</sub> = s<sub>i</sub>² mod n für i = 1, 2, ..., k</li>
          <li>Die öffentlichen Werte v<sub>i</sub> werden publiziert</li>
        </ol>

        <h3>Protokoll (für jede Runde)</h3>
        <ol>
          <li><strong>Commitment:</strong> Peggy wählt zufälliges r (coprime zu n und idealeweise größer als √n) und sendet x = r² mod n an Victor</li>
          <li><strong>Challenge:</strong> Victor wählt zufällig e ∈ {0, 1} und bei e=1 auch j ∈ {1, 2, ..., k}</li>
          <li><strong>Antwort:</strong> Peggy sendet y = r (falls e=0) oder y = r·s<sub>j</sub> mod n (falls e=1)</li>
          <li><strong>Verifikation:</strong> Victor prüft, ob y² mod n = x (falls e=0) oder y² mod n = x·v<sub>j</sub> mod n (falls e=1)</li>
        </ol>

        <h3>Sicherheit</h3>
        <p>
          Nach t erfolgreichen Runden ist die Wahrscheinlichkeit, dass Peggy schummelt, höchstens (1/2)<sup>t</sup>.
          Das Protokoll ist "zero-knowledge", da Victor keine Information über die geheimen Werte s<sub>i</sub> erhält.
        </p>

        <h3>Sicherheitshinweise</h3>
        <ul>
          <li>Peggy muss in jeder Runde ein neues zufälliges r wählen, um die Sicherheit zu gewährleisten.</li>
          <li>Sowohl die geheimen Werte s<sub>i</sub> als auch die zufälligen Werte r sollten idealerweise größer als √n sein, um einige Angriffsvektoren zu vermeiden.</li>
        </ul>
      </div>
    </div>
  </div>
</template>

<script>
// In einer echten Implementierung würden wir eine Bibliothek für Multipräzisionsarithmetik verwenden
// wie z.B. big-integer oder bn.js
export default {
  name: 'FFSDemo',
  data() {
    return {
      k: 3, // Anzahl der Zahlen
      n: 143, // Modulus (sollte das Produkt zweier großer Primzahlen sein)
      setup: {
        complete: false,
        secretValues: [], // s_i values
        publicValues: []  // v_i values
      },
      rounds: [],
      currentRound: {},
      roundActive: false,
      currentStep: null
    };
  },
  methods: {
    // Hilfsfunktion zur Berechnung des größten gemeinsamen Teilers (ggT)
    gcd(a, b) {
      if (b === 0) return a;
      return this.gcd(b, a % b);
    },

    // Hilfsfunktion zur Prüfung, ob eine Zahl coprime zu einer anderen ist
    isCoprime(a, n) {
      return this.gcd(a, n) === 1;
    },

    // Hilfsfunktion zur Prüfung, ob eine Zahl ein Blum-Integer ist
    // Ein Blum-Integer ist ein Produkt zweier Primzahlen p, q mit p ≡ 3 mod 4 und q ≡ 3 mod 4
    // Für die Demo akzeptieren wir auch andere Zahlen, geben aber eine Warnung aus
    checkBlumInteger(n) {
      // Vereinfachte Prüfung für die Demo
      if (n < 10) {
        return false;
      }
      return true;
    },

    // Setup-Phase des Protokolls
    generateValues() {
      if (this.k < 1 || this.n < 10) {
        alert('Bitte gültige Werte für k und n eingeben.');
        return;
      }

      // Prüfen, ob n ein Blum-Integer ist (vereinfacht für die Demo)
      if (!this.checkBlumInteger(this.n)) {
        alert('Warnung: Der Modulus n sollte idealerweise ein Blum-Integer sein (Produkt zweier Primzahlen p, q mit p ≡ 3 mod 4 und q ≡ 3 mod 4).');
      }

      const secretValues = [];
      const publicValues = [];

      // In einer echten Implementierung würden wir hier Primzahlentests etc. durchführen
      for (let i = 0; i < this.k; i++) {
        // Wähle zufälligen geheimen Wert s_i, der coprime zu n ist
        let s;
        do {
          // s sollte nicht zu klein sein (laut den Slides)
          s = this.randomInt(this.n, Math.ceil(Math.sqrt(this.n)));
        } while (!this.isCoprime(s, this.n));

        secretValues.push(s);

        // Berechne öffentlichen Wert v_i = s_i^2 mod n
        const v = this.modPow(s, 2, this.n);
        publicValues.push(v);
      }

      this.setup = {
        complete: true,
        secretValues,
        publicValues
      };

      this.rounds = [];
      this.roundActive = false;
      this.currentStep = null;
    },

    // Starte eine neue Protokollrunde
    startNewRound() {
      this.roundActive = true;
      this.currentStep = 'start';
      this.currentRound = {};
    },

    // Schritt 1: Peggy erstellt Commitment
    createCommitment() {
      // Peggy wählt zufälliges r, das coprime zu n ist
      let r;
      do {
        // r sollte nicht zu klein sein (laut den Slides)
        r = this.randomInt(this.n, Math.ceil(Math.sqrt(this.n)));
      } while (!this.isCoprime(r, this.n));

      // Berechnet x = r^2 mod n
      const x = this.modPow(r, 2, this.n);

      this.currentRound = {
        r: r,  // Geheimer Wert, den Peggy wählt
        x: x   // Commitment, das Peggy an Victor sendet
      };

      this.currentStep = 'commitment';
    },

    // Schritt 2: Victor sendet Challenge
    createChallenge() {
      // Victor wählt zufällig e aus {0, 1}
      const e = Math.random() < 0.5 ? 0 : 1;

      // Wenn e=1, wählt Victor auch j aus {1, 2, ..., k}
      const j = e === 1 ? Math.floor(Math.random() * this.k) + 1 : null;

      this.currentRound.e = e;
      this.currentRound.j = j;

      this.currentStep = 'challenge';
    },

    // Schritt 3: Peggy sendet Antwort
    createResponse() {
      if (this.currentRound.e === 0) {
        // Wenn e=0, sendet Peggy y = r
        this.currentRound.y = this.currentRound.r;
      } else {
        // Wenn e=1, sendet Peggy y = r * s_j mod n
        const j = this.currentRound.j - 1; // Umrechnung auf 0-basierte Indizierung
        const s_j = this.setup.secretValues[j];
        this.currentRound.y = this.modMul(this.currentRound.r, s_j, this.n);
      }

      this.currentStep = 'response';
    },

    // Schritt 4: Victor verifiziert
    verifyResponse() {
      const y_squared = this.modPow(this.currentRound.y, 2, this.n);
      let verification_right;

      if (this.currentRound.e === 0) {
        // Wenn e=0, prüfe ob y^2 mod n = x
        verification_right = this.currentRound.x;
      } else {
        // Wenn e=1, prüfe ob y^2 mod n = x * v_j mod n
        const j = this.currentRound.j - 1; // Umrechnung auf 0-basierte Indizierung
        const v_j = this.setup.publicValues[j];
        verification_right = this.modMul(this.currentRound.x, v_j, this.n);
      }

      // Speichere Verifikationsinformationen für die Anzeige
      this.currentRound.verificationLeft = y_squared;
      this.currentRound.verificationRight = verification_right;

      // Prüfe ob die Gleichung erfüllt ist
      this.currentRound.verified = y_squared === verification_right;

      this.currentStep = 'verification';

      // Füge die aktuelle Runde zur Historie hinzu
      this.rounds.push({...this.currentRound});

      // Beende die aktuelle Runde
      this.roundActive = false;
    },

    // Zurücksetzen des Demos
    resetDemo() {
      this.setup.complete = false;
      this.rounds = [];
      this.currentRound = {};
      this.roundActive = false;
      this.currentStep = null;
    }
  }
};
</script>

